import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import DataService from '../services/DataService';

function CreateDataComponent() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [displayName, setDisplayName] = useState("");
  const [displayImg, setDisplayImg] = useState("");

  useEffect(() => {
    if (id === "-add") {
      return;
    } else {
      DataService.getDataById(id).then((res) => {
        let data = res.data;
        setUsername(data.username);
        setDisplayName(data.displayName);
        setDisplayImg(data.displayImg);
      });
    }
  }, [id]);

  const saveOrUpdateData = (e) => {
    e.preventDefault();
    let newData;
 
    if (id === "-add") {
      // For new data entry
      newData = {
        username,
        displayName,
        displayImg,
        JSON: '{"testJ": "testS", "testO": "testN"}', // Your JSON string
        // Add other fields if needed
      };
    } else {
      // For updating existing data
      DataService.getDataById(id).then((res) => {
        const existingData = res.data;
 
        newData = {
          JSON: existingData.JSON, // If you want to keep the existing JSON value
          username,
          displayName,
          displayImg,
          // Add other fields if needed
        };
 
        DataService.updateData(newData, id).then(() => {
          navigate('/data');
        });
      });
      return;
    }
 
    // Create a new data entry in the database or update existing data
    DataService.createData(newData).then(() => {
      navigate('/data');
    });
  };
 
 

  const changeUsernameHandler = (event) => {
    setUsername(event.target.value);
  };

  const cancel = () => {
    navigate('/data');
  };

  const getTitle = () => {
    if (id === "-add") {
      return <h3 className="text-center">Add Profile</h3>;
    } else {
      return <h3 className="text-center">Update</h3>;
    }
  };

  return (
    <div>
      <br></br>
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            {getTitle()}
            <div className="card-body">
              <form>
                <div className="form-group">
                  <label> Username: </label>
                  <input
                    placeholder="Username"
                    name="username"
                    className="form-control"
                    value={username}
                    onChange={changeUsernameHandler}
                  />
                </div>

                <div className="form-group">
                  <label> Display Name: </label>
                  <input
                    placeholder="Display Name"
                    name="displayName"
                    className="form-control"
                    value={displayName}
                    onChange={(e) => setDisplayName(e.target.value)}
                  />
                </div>

                <div className="form-group">
                  <label> Display Image: </label>
                  <input
                    placeholder="img.jpg"
                    name="displayImg"
                    className="form-control"
                    value={displayImg}
                    onChange={(e) => setDisplayImg(e.target.value)}
                  />
                </div>

                <button className="btn btn-success" onClick={saveOrUpdateData}>
                  Save
                </button>
                <button
                  className="btn btn-danger"
                  onClick={cancel}
                  style={{ marginLeft: "10px" }}
                >
                  Cancel
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CreateDataComponent;
